import './App.css';
import React, { useEffect, useState } from 'react';
import kontorer from './kontor.json';
import { db } from './firebase';
import { uid } from 'uid';
import { set, ref, onValue } from "firebase/database"




const App = () => {
  const [tittel, setTittel] = useState('');
  const [innhold, setInnhold] = useState('');
  const [forfatter, setForfatter] = useState('');
  const [kontor, setKontor] = useState('');
  const [merke, setMerke] = useState('');



  var kontorerLand = []
  for (let i = 0; i < kontorer.length; i++) {
    kontorerLand.push(kontorer[i].country)
  }

  //create
  const writeToDatabase = (e) => {
    e.preventDefault()
    const uuid = uid()
    set(ref(db, `/beskjeder`), {
      tittel: tittel,
      innhold: innhold,
      forfatter: forfatter,
      kontor: kontor,
      merke: merke,
      uuid: uuid,
    })
    setTittel('')
    setInnhold('')
    setForfatter('')
    setKontor('')
    setMerke('')
    window.alert("Beskjed ble sendt!")
    setAktivSide(MsgBoard)

  }

  const handleTittel = (e) => {
    setTittel(e.target.value)
  }

  const handleInnhold = (e) => {
    setInnhold(e.target.value)
  }

  const handleKontor = (e) => {
    setKontor(e.target.value);
  }

  const handleForfatter = (e) => {
    setForfatter(e.target.value)
  }

  const handleMerke =(e) => {
    setMerke(e.target.value)
  }

  var ikkeViktig = null
  var liteViktig = null
  var viktig = null



  const SkrivNyBeskjed = () => {
    

    return (
      <>
        <div className="innhold">
          <div id="topp_innhold">
            <h1>Skriv ny beskjed</h1>
            <hr id="strek"></hr>
          </div>
          <form id="send_beskjed">
            <p>Skriv en ny beskjed til de andre.</p>

              <input value={tittel} onChange={handleTittel} className='tekst_input' placeholder='Overskrift/Tittel' type="text"></input>
              <textarea value={innhold} onChange={handleInnhold} id="tekstboks"></textarea>
              <input value={forfatter} onChange={handleForfatter} className='tekst_input' placeholder='Forfatter' type="text"></input>
              <select className='knapp' id="velg_kontor" onChange={handleKontor} value={kontor}>
                {kontorerLand.map((x, y) => <option key={y}>{x}</option>)}
              </select>
              <select className='knapp' onChange={handleMerke} value={merke}>
                <option value={ikkeViktig}>Ikke viktig</option>
                <option value={liteViktig}>Lite viktig</option>
                <option value={viktig}>Viktig</option>
              </select>
              <button onClick={writeToDatabase} className='knapp'>Send beskjed</button>
              <button className='knapp' onClick={() => setAktivSide(MsgBoard)}>Avrbyt</button>


          </form>
        </div>

      </>
    )
  }


  const MsgBoard = () => {

    //Read
    const [alleTitler, setAlleTitler] = useState([]);

    useEffect(() => {
      onValue(ref(db), snapshot => {
        const data = snapshot.val();
        if (data !== null) {
          setAlleTitler((oldArray) =>
            Object.values(data).reduce((acc, kontor) => {
              if (kontor !== null) {
                return [...acc, kontor];
              }
              return acc;
            }, oldArray)
          );
        }
      });
    }, []);
    

    const [altInnhold, setAltInnhold] = useState([]);

    useEffect(() => {
      onValue(ref(db), snapshot => {
        const data = snapshot.val();
        if (data !== null) {
          setAltInnhold((oldArray) =>
            Object.values(data).reduce((acc, innhold) => {
              if (innhold !== null) {
                return [...acc, innhold];
              }
              return acc;
            }, oldArray)
          );
        }
      });
    }, []);
    

    const [alleKontorer, setAlleKontorer] = useState([]);

    useEffect(() => {
      onValue(ref(db), snapshot => {
        const data = snapshot.val();
        if (data !== null) {
          setAlleKontorer((oldArray) =>
            Object.values(data).reduce((acc, kontor) => {
              if (kontor !== null) {
                return [...acc, kontor];
              }
              return acc;
            }, oldArray)
          );
        }
      });
    }, []);
    

    const [alleMerker, setAlleMerker] = useState([]);

    useEffect(() => {
      onValue(ref(db), snapshot => {
        const data = snapshot.val();
        if (data !== null) {
          setAlleMerker((oldArray) =>
            Object.values(data).reduce((acc, merke) => {
              if (merke !== null) {
                return [...acc, merke];
              }
              return acc;
            }, oldArray)
          );
        }
      });
    }, []);
    
    return (
      <>

        <div className="innhold">
          <div id="topp">
            {/*<select className="knapp" id="filter_beskjeder">
              <option>Filter</option>
              <option id="important">Viktig</option>
              <option id="kinda_important">Litt viktig</option>
              <option id="unimportant">Ikke viktig</option>
    </select>*/}
    <p>kommer snart</p>
            <h1 id="overskrift">Beskjeder</h1>
            <button id="ny_beskjed" className="knapp" onClick={() => setAktivSide(NBikkeLoggetInn)}>Ny beskjed</button>
          </div>
          <hr id="strek"></hr>
         
        </div>
      </>


    );
  }



  const NBikkeLoggetInn = () => {
    return (
      <>
        <div className="innhold">
          <SkrivNyBeskjed />
        </div>
      </>
    )
  }
  const [aktivSide, setAktivSide] = useState(MsgBoard);
  const [loggetInn, setLoggetInn] = useState(NBikkeLoggetInn);

  return (
    <>
      {/* Bytter sider */}
      {aktivSide}
    </>
  );
}

export default App;
