import React, { useState, useEffect } from 'react';

const MonkeyType = () => {
  const [text, setText] = useState('');
  const [input, setInput] = useState('');
  const [isCorrect, setIsCorrect] = useState(true);
  const [time, setTime] = useState(0);
  const [timer, setTimer] = useState(null);

  useEffect(() => {
    if (!text) {
      fetch('https://loripsum.net/api')
        .then(res => res.text())
        .then(setText);
    }
  }, [text]);

  const handleInput = e => {
    setInput(e.target.value);
    if (input === text.substr(0, input.length)) {
      setIsCorrect(true);
    } else {
      setIsCorrect(false);
    }
  };

  const startTimer = () => {
    setTimer(
      setInterval(() => {
        setTime(time + 1);
      }, 1000)
    );
  };

  const stopTimer = () => {
    clearInterval(timer);
  };

  return (
    <div className="monkey-type">
      <div className="text-area">
        <p>{text}</p>
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onFocus={startTimer}
          onBlur={stopTimer}
          onChange={handleInput}
        />
        <p className={`${isCorrect ? 'correct' : 'incorrect'}`}>
          {isCorrect ? 'Correct' : 'Incorrect'}
        </p>
      </div>
      <div className="timer">
        <p>Time: {time}s</p>
      </div>
    </div>
  );
};

export default MonkeyType;
