import './App.css';
import React, { useState } from 'react';
import { useEffect } from 'react';
import kontorer from './kontor.json';
import { db } from './firebase';
import { uid } from 'uid';
import { set, ref, onValue, off } from "firebase/database"

const express = require('express');
const app = express();
const port = 3000;

app.use(express.urlencoded({ extended: true }));

app.get('/', (req, res) => {
  res.send(`
    <form method="POST" action="/">
      <label for="message">Message:</label>
      <input type="text" id="message" name="message">
      <button type="submit">Send Message</button>
    </form>
  `);
});

app.post('/', (req, res) => {
  const message = req.body.message;
  skrivnybeskjed(message); // call the skrivnybeskjed function to handle the form submission
  res.send(`Message "${message}" sent successfully!`);
});

function skrivnybeskjed(message) {
  // implementation of the skrivnybeskjed function
}

app.listen(port, () => {
  console.log(`Example app listening at http://localhost:${port}`);
});


const SkrivNyBeskjed = ({ setAktivSide }) => {

  const [tittel, setTittel] = useState('');
  const [innhold, setInnhold] = useState('');
  const [forfatter, setForfatter] = useState('');
  const [kontor, setKontor] = useState('');
  const [merke, setMerke] = useState('');



  var kontorerLand = []
  for (let i = 0; i < kontorer.length; i++) {
    kontorerLand.push(kontorer[i].country)
  }

  //create
  const writeToDatabase = (e) => {
    e.preventDefault()
    const uuid = uid()
    set(ref(db, `/${uuid}`), {
      tittel: tittel,
      innhold: innhold,
      forfatter: forfatter,
      kontor: kontor,
      merke: merke,
      uuid: uuid,
    })
    setTittel('')
    setInnhold('')
    setForfatter('')
    setKontor('')
    setMerke('')
    window.alert("Beskjed ble sendt!")
    setAktivSide(MsgBoard)

  }

  const handleTittel = (e) => {
    setTittel(e.target.value)
  }

  const handleInnhold = (e) => {
    setInnhold(e.target.value)
  }

  const handleKontor = (e) => {
    setKontor(e.target.value);
  }

  const handleForfatter = (e) => {
    setForfatter(e.target.value)
  }

  const handleMerke = (e) => {
    setMerke(e.target.value)
  }

  var ikkeViktig = null
  var liteViktig = null
  var viktig = null


  return (
    <>
      <div className="innhold">
        <div id="topp_innhold">
          <h1>Skriv ny beskjed</h1>
          <hr id="strek"></hr>
        </div>
        <form id="send_beskjed">
          <p>Skriv en ny beskjed til de andre.</p>

          <input value={tittel} onChange={handleTittel} className='tekst_input' placeholder='Overskrift/Tittel' type="text"></input>
          <textarea value={innhold} onChange={handleInnhold} id="tekstboks"></textarea>
          <input value={forfatter} onChange={handleForfatter} className='tekst_input' placeholder='Forfatter' type="text"></input>
          <select className='knapp' id="velg_kontor" onChange={handleKontor} value={kontor}>
            {kontorerLand.map((x, y) => <option key={y}>{x}</option>)}
          </select>
          <select className='knapp' onChange={handleMerke} value={merke}>
            <option>velg merke</option>
            <option value={ikkeViktig}>Ikke viktig</option>
            <option value={liteViktig}>Lite viktig</option>
            <option value={viktig}>Viktig</option>
          </select>
          <button onClick={writeToDatabase} className='knapp'>Send beskjed</button>
          <button className='knapp' onClick={() => setAktivSide(MsgBoard)}>Avrbyt</button>


        </form>
      </div>
      
    </>
  )
}


const MsgBoard = ({ setAktivSide }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const messagesRef = ref(db);
    onValue(messagesRef, (snapshot) => {
      const messagesObject = snapshot.val();
      if (messagesObject) {
        const messagesList = Object.keys(messagesObject).map((key) => ({
          ...messagesObject[key],
          uuid: key,
        }));
        setMessages(messagesList);
      }
    });
    return () => off(messagesRef);
  }, []);

  return (
    <>
      <div className="innhold">
        <div id="topp_innhold">
          <h1>Meldingsbrett</h1>
          <hr id="strek"></hr>
        </div>
        <div id="beskjeder">
          {messages.map((message) => (
            <>
              key={message.uuid}
              tittel={message.tittel}
              innhold={message.innhold}
              forfatter={message.forfatter}
              kontor={message.kontor}
              merke={message.merke}
              uuid={message.uuid}
            </>
          ))}
        </div>
        <button className='knapp' onClick={() => setAktivSide(SkrivNyBeskjed)}>Skriv ny beskjed</button>
      </div>
    </>
  )
}

const App = () => {
  const [aktivSide, setAktivSide] = useState(MsgBoard);

  return (
    <>
      {aktivSide === MsgBoard ? (
        <MsgBoard setAktivSide={setAktivSide} />
      ) : (
        <SkrivNyBeskjed setAktivSide={setAktivSide} />
      )}
    </>
  );
};

export default App;
